import { initDepartment, loadDepartment, addDepartment, editDepartment, deleteDepartment, searchDepartment, getUserByDepartmentId } from "@/api/index";
export default {
  name: "department-manage",
  data: function data() {
    return {
      loading: true,
      maxHeight: "500px",
      strict: true,
      userLoading: false,
      loadingEdit: true,
      modalVisible: false,
      selectList: [],
      selectCount: 0,
      showParent: false,
      modalTitle: "",
      editTitle: "",
      searchKey: "",
      form: {
        id: "",
        title: "",
        parentId: "",
        parentTitle: "",
        sortOrder: 0,
        status: 0
      },
      formAdd: {},
      formValidate: {
        title: [{
          required: true,
          message: "名称不能为空",
          trigger: "blur"
        }]
      },
      submitLoading: false,
      data: [],
      dataEdit: [],
      users: []
    };
  },
  methods: {
    init: function init() {
      this.getParentList();
      this.getParentListEdit();
    },
    getParentList: function getParentList() {
      var _this = this;

      this.loading = true;
      initDepartment().then(function (res) {
        _this.loading = false;

        if (res.success === true) {
          res.result.forEach(function (e) {
            if (e.isParent) {
              e.loading = false;
              e.children = [];
            }
          });
          _this.data = res.result;
        }
      });
    },
    getParentListEdit: function getParentListEdit() {
      var _this2 = this;

      this.loadingEdit = true;
      initDepartment().then(function (res) {
        _this2.loadingEdit = false;

        if (res.success === true) {
          res.result.forEach(function (e) {
            if (e.isParent) {
              e.loading = false;
              e.children = [];
            }
          }); // 头部加入一级

          var first = {
            id: "0",
            title: "一级部门"
          };
          res.result.unshift(first);
          _this2.dataEdit = res.result;
        }
      });
    },
    loadData: function loadData(item, callback) {
      loadDepartment(item.id).then(function (res) {
        if (res.success === true) {
          res.result.forEach(function (e) {
            if (e.isParent) {
              e.loading = false;
              e.children = [];
            }
          });
          callback(res.result);
        }
      });
    },
    search: function search() {
      var _this3 = this;

      if (this.searchKey) {
        this.loading = true;
        searchDepartment({
          title: this.searchKey
        }).then(function (res) {
          _this3.loading = false;

          if (res.success) {
            _this3.data = res.result;
          }
        });
      } else {
        this.getParentList();
      }
    },
    selectTree: function selectTree(v) {
      var _this4 = this;

      if (v.length > 0) {
        // 转换null为""
        for (var attr in v[0]) {
          if (v[0][attr] === null) {
            v[0][attr] = "";
          }
        }

        var str = JSON.stringify(v[0]);
        var data = JSON.parse(str);
        this.editTitle = data.title; // 加载部门用户数据

        this.userLoading = true;
        getUserByDepartmentId(data.id).then(function (res) {
          _this4.userLoading = false;

          if (res.success) {
            _this4.users = res.result;
          }
        }); // 回显

        this.form = data;
      } else {
        this.cancelEdit();
      }
    },
    cancelEdit: function cancelEdit() {
      var data = this.$refs.tree.getSelectedNodes()[0];

      if (data) {
        data.selected = false;
      }

      this.$refs.form.resetFields();
      delete this.form.id;
      this.editTitle = "";
    },
    selectTreeEdit: function selectTreeEdit(v) {
      if (v.length > 0) {
        // 转换null为""
        for (var attr in v[0]) {
          if (v[0][attr] === null) {
            v[0][attr] = "";
          }
        }

        var str = JSON.stringify(v[0]);
        var data = JSON.parse(str);
        this.form.parentId = data.id;
        this.form.parentTitle = data.title;
      }
    },
    cancelAdd: function cancelAdd() {
      this.modalVisible = false;
    },
    handleReset: function handleReset() {
      this.$refs.form.resetFields();
      this.form.status = 0;
    },
    submitEdit: function submitEdit() {
      var _this5 = this;

      this.$refs.form.validate(function (valid) {
        if (valid) {
          if (!_this5.form.id) {
            _this5.$Message.warning("请先点击选择要修改的部门");

            return;
          }

          _this5.submitLoading = true;
          editDepartment(_this5.form).then(function (res) {
            _this5.submitLoading = false;

            if (res.success === true) {
              _this5.$Message.success("编辑成功");

              _this5.init();

              _this5.modalVisible = false;
            }
          });
        }
      });
    },
    submitAdd: function submitAdd() {
      var _this6 = this;

      this.$refs.formAdd.validate(function (valid) {
        if (valid) {
          _this6.submitLoading = true;
          addDepartment(_this6.formAdd).then(function (res) {
            _this6.submitLoading = false;

            if (res.success === true) {
              _this6.$Message.success("添加成功");

              _this6.init();

              _this6.modalVisible = false;
            }
          });
        }
      });
    },
    add: function add() {
      if (this.form.id == "" || this.form.id == null) {
        this.$Message.warning("请先点击选择一个部门");
        return;
      }

      this.modalTitle = "添加子部门";
      this.showParent = true;
      this.formAdd = {
        parentId: this.form.id,
        sortOrder: 0,
        status: 0
      };
      this.modalVisible = true;
    },
    addRoot: function addRoot() {
      this.modalTitle = "添加一级部门";
      this.showParent = false;
      this.formAdd = {
        parentId: 0,
        sortOrder: 0,
        status: 0
      };
      this.modalVisible = true;
    },
    changeSelect: function changeSelect(v) {
      this.selectCount = v.length;
      this.selectList = v;
    },
    delAll: function delAll() {
      var _this7 = this;

      if (this.selectCount <= 0) {
        this.$Message.warning("您还未勾选要删除的数据");
        return;
      }

      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
        onOk: function onOk() {
          var ids = "";

          _this7.selectList.forEach(function (e) {
            ids += e.id + ",";
          });

          ids = ids.substring(0, ids.length - 1);
          deleteDepartment(ids).then(function (res) {
            if (res.success === true) {
              _this7.$Message.success("删除成功");

              _this7.selectList = [];
              _this7.selectCount = 0;

              _this7.cancelEdit();

              _this7.init();
            }
          });
        }
      });
    }
  },
  mounted: function mounted() {
    // 计算高度
    var height = document.documentElement.clientHeight;
    this.maxHeight = Number(height - 287) + "px";
    this.init();
  }
};
