      printJS("printjs", "html");
    },
    printJson: function printJson() {
      printJS({
        printable: this.data,
        header: "用户数据",
        gridStyle: "border: 1px solid lightgray;text-align:center",
        properties: ["id", "name", "createTime", "updateTime"],
        type: "json"
      });
    },
    html2canvas: function html2canvas() {
      var that = this;

      _html2canvas(document.body).then(function (canvas) {
        that.imgUrl = canvas.toDataURL("image/jpeg");
      });

      this.modalVisible = true;
      this.showPrintImage = false;
    },
    html2canvas2: function html2canvas2() {
      var that = this;

      _html2canvas(document.getElementById("html2canvas")).then(function (canvas) {
        that.imgUrl = canvas.toDataURL("image/jpeg");
      });

      this.modalVisible = true;
      this.showPrintImage = false;
    },
    html2canvas3: function html2canvas3() {
      var that = this;

      _html2canvas(document.body).then(function (canvas) {
        that.imgUrl = canvas.toDataURL("image/jpeg");
      });

      this.modalVisible = true;
      this.showPrintImage = true;
    },
    printPic: function printPic() {
      printJS({
        printable: "render",
        type: "html",
        header: "图片数据"
      });
    },
