      this.$Message.success("复制成功");
    },
    onError: function onError() {
      this.$Message.warning("复制失败，请手动复制");
    },
    handleRadioClick: function handleRadioClick(v) {
      this.selectCount = this.$refs.treeTable.getCheckedProp().length;
    },
    click: function click(v) {
      this.$Message.info("点击数据ID为：" + v.id);
    },
    initVideo: function initVideo(v) {
      var dp = new DPlayer({
        container: document.getElementById("dplayer"),
        screenshot: true,
        video: {
          url: "https://cloud.video.taobao.com//play/u/95292294/p/1/e/6/t/1/210877258798.mp4"
        },
        danmaku: {
          id: "弹幕ID210877258798",
          api: "https://api.prprpr.me/dplayer/"
        }
      });
    },
    // 实时预览
    realTime: function realTime(data) {
      var preview = data,
          h = 0.5;
      this.previewStyle = {
        width: preview.w + "px",
        height: preview.h + "px",
        overflow: "hidden",
        margin: "0",
        zoom: h
      };
      this.previews = data;
    },
    upload: function upload() {
      var _this = this;

      // 获取截图的base64 数据
      this.$refs.cropper.getCropData(function (data) {
        _this.uploadLoading = true;
        base64Upload({
          base64: data
        }).then(function (res) {
          _this.uploadLoading = false;

          if (res.success) {
            _this.$Message.success("上传成功");
          }
        });
      });
    }
  },
  mounted: function mounted() {
    this.init();
  }
