import Vue from 'vue';
import Locales from './locale';
import zhLocale from 'iview/src/locale/lang/zh-CN';
import enLocale from 'iview/src/locale/lang/en-US'; // 自动设置语言

var navLang = navigator.language;
var localLang = navLang === 'zh-CN' || navLang === 'en-US' ? navLang : false;
var lang = window.localStorage.lang || localLang || 'zh-CN';
Vue.config.lang = lang; // 多语言配置

var locales = Locales;
var mergeZH = Object.assign(zhLocale, locales['zh-CN']);
var mergeEN = Object.assign(enLocale, locales['en-US']);
Vue.locale('zh-CN', mergeZH);
Vue.locale('en-US', mergeEN);
