import { getRequest, postRequest, putRequest, deleteRequest, uploadFileRequest } from '@/libs/axios'; // 获取内容数据 多条件

export var getWechatContentList = function getWechatContentList(params) {
  return getRequest('/content/getByCondition', params);
}; // 删除内容

export var deleteContent = function deleteContent(ids, params) {
  return deleteRequest("/content/delByIds/".concat(ids), params);
}; // 编辑

export var editContent = function editContent(params) {
  return postRequest('/content/content/edit', params);
}; // 保存内容

export var saveContent = function saveContent(params) {
  return postRequest('/content/content/add', params);
}; // 通过id获取单个内容

export var getWechatContent = function getWechatContent(id) {
  return getRequest("/content/content/get/".concat(id));
}; // 通过内容类别获取最新单个内容

export var getContentByContentType = function getContentByContentType(contentType) {
  return getRequest("/content/content/getInfo/".concat(contentType));
};
