import { initDepartment, loadDepartment } from "@/api/index";
export default {
  name: "departmentChoose",
  props: {
    width: {
      type: String,
      default: "200px"
    }
  },
  data: function data() {
    return {
      selectDep: [],
      department: []
    };
  },
  methods: {
    initDepartmentData: function initDepartmentData() {
      var _this = this;

      initDepartment().then(function (res) {
        if (res.success === true) {
          res.result.forEach(function (e) {
            if (e.isParent) {
              e.value = e.id;
              e.label = e.title;
              e.loading = false;
              e.children = [];
            } else {
              e.value = e.id;
              e.label = e.title;
            }

            if (e.status === -1) {
              e.label = "[已禁用] " + e.label;
              e.disabled = true;
            }
          });
          _this.department = res.result;
        }
      });
    },
    loadData: function loadData(item, callback) {
      item.loading = true;
      loadDepartment(item.value).then(function (res) {
        item.loading = false;

        if (res.success === true) {
          res.result.forEach(function (e) {
            if (e.isParent) {
              e.value = e.id;
              e.label = e.title;
              e.loading = false;
              e.children = [];
            } else {
              e.value = e.id;
              e.label = e.title;
            }

            if (e.status === -1) {
              e.label = "[已禁用] " + e.label;
              e.disabled = true;
            }
          });
          item.children = res.result;
          callback();
        }
      });
    },
    handleChangeDep: function handleChangeDep(value, selectedData) {
      var departmentId = ""; // 获取最后一个值

      if (value && value.length > 0) {
        departmentId = value[value.length - 1];
      }

      this.$emit("on-select", departmentId);
    },
    clearSelect: function clearSelect() {
      this.selectDep = [];
    }
  },
  created: function created() {
    this.initDepartmentData();
  }
};
