import { getMenuTemplatePage, addMenuTemplate, editMenuTemplate, delMenuTemplate } from "@/api/wechat";
import { getDictDataByType } from "@/api/index";
import { debug } from 'util';
export default {
  name: "builder-code",
  components: {},
  data: function data() {
    var _this = this;

    return {
      loading: true,
      operationLoading: false,
      selectCount: 0,
      selectList: [],
      searchKey: "",
      searchForm: {
        pageNumber: 1,
        pageSize: 10,
        sort: "createTime",
        order: "desc",
        name: ""
      },
      //窗口
      modalType: 0,
      modalVisible: false,
      modalTitle: "",
      formItem: {
        name: '',
        menuJson: '',
        description: ''
      },
      formItemValidate: {
        name: [{
          required: true,
          message: "模版名称不能为空",
          trigger: "blur"
        }]
      },
      submitLoading: false,
      //table 列表字段
      columns: [{
        type: "selection",
        width: 60,
        align: "center",
        fixed: "left"
      }, {
        type: "index",
        width: 60,
        align: "center"
      }, {
        title: "模版名称",
        key: "name",
        minWidth: 120,
        sortable: true
      }, {
        title: "描述",
        key: "description",
        minWidth: 100
      }, {
        title: "创建时间",
        key: "createTime",
        sortable: true,
        sortType: "desc",
        width: 150
      }, {
        title: "操作",
        key: "action",
        width: 220,
        align: "center",
        fixed: "right",
        render: function render(h, params) {
          return h("div", [h("Button", {
            props: {
              type: "success",
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.editMenu(params.row);
              }
            }
          }, "自定义菜单"), h("Button", {
            props: {
              type: "primary",
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.edit(params.row);
              }
            }
          }, "编辑"), h("Button", {
            props: {
              type: "error",
              size: "small"
            },
            on: {
              click: function click() {
                _this.remove(params.row);
              }
            }
          }, "删除")]);
        }
      }],
      data: [],
      total: 0,
      dictAccountType: []
    };
  },
  methods: {
    init: function init() {
      this.getList();
      this.getDictAccountTypeData();
    },
    //加载数字字典
    getDictAccountTypeData: function getDictAccountTypeData() {
      var _this2 = this;

      // 获取性别字典数据
      getDictDataByType("wx_account_type").then(function (res) {
        if (res.success) {
          _this2.dictAccountType = res.result;
        }
      });
    },
    //翻页
    changePage: function changePage(v) {
      this.searchForm.pageNumber = v;
      this.getList();
      this.clearSelectAll();
    },
    //每页条数
    changePageSize: function changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getList();
    },
    //排序
    changeSort: function changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;

      if (e.order === "normal") {
        this.searchForm.order = "";
      }

      this.getList();
    },
    showSelect: function showSelect(e) {
      this.exportData = e;
      this.selectList = e;
      this.selectCount = e.length;
    },
    //清空
    clearSelectAll: function clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    //搜索
    handleSearch: function handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.getList();
    },
    //重置
    handleReset: function handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10; // 重新加载数据

      this.getList();
    },
    //查询列表
    getList: function getList() {
      var _this3 = this;

      // 多条件搜索用户列表
      this.loading = true;
      getMenuTemplatePage(this.searchForm).then(function (res) {
        _this3.loading = false;

        if (res.success === true) {
          _this3.data = res.result.content;
          _this3.total = res.result.totalElements;
        }
      });
    },
    //编辑
    editMenu: function editMenu(v) {
      this.$router.push({
        name: 'customMenu',
        query: {
          id: v.id,
          name: v.name
        }
      });
    },
    //新增按钮操作
    add: function add() {
      this.modalType = 0;
      this.modalTitle = "新增";
      this.$refs.formItem.resetFields();
      this.modalVisible = true;
    },
    //编辑
    edit: function edit(v) {
      this.modalType = 1;
      this.modalTitle = "编辑";
      this.$refs.formItem.resetFields(); // 转换null为""

      for (var attr in v) {
        if (v[attr] === null) {
          v[attr] = "";
        }
      }

      var str = JSON.stringify(v);
      var data = JSON.parse(str);
      this.formItem = data;
      this.modalVisible = true;
    },
    //取消
    cancelBt: function cancelBt() {
      this.modalVisible = false;
    },
    //提交
    submitBt: function submitBt() {
      var _this4 = this;

      this.$refs.formItem.validate(function (valid) {
        if (valid) {
          //添加
          if (_this4.modalType === 0) {
            // 添加 避免编辑后传入id
            delete _this4.formItem.id;
            _this4.submitLoading = true;
            addMenuTemplate(_this4.formItem).then(function (res) {
              _this4.submitLoading = false;

              if (res.success === true) {
                _this4.$Message.success(res.message);

                _this4.getList();

                _this4.modalVisible = false;
              }
            });
          } else {
            // 编辑
            _this4.submitLoading = true;
            editMenuTemplate(_this4.formItem).then(function (res) {
              _this4.submitLoading = false;

              if (res.success === true) {
                _this4.$Message.success("操作成功");

                _this4.getList();

                _this4.modalVisible = false;
              }
            });
          }
        }
      });
    },
    //批量删除
    delAll: function delAll() {
      var _this5 = this;

      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }

      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
        onOk: function onOk() {
          var ids = "";

          _this5.selectList.forEach(function (e) {
            ids += e.id + ",";
          });

          ids = ids.substring(0, ids.length - 1);
          _this5.operationLoading = true;
          delMenuTemplate(ids).then(function (res) {
            _this5.operationLoading = false;

            if (res.success === true) {
              _this5.$Message.success("删除成功");

              _this5.clearSelectAll();

              _this5.getList();
            }
          });
        }
      });
    },
    //单个删除
    remove: function remove(v) {
      var _this6 = this;

      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除【" + v.name + "】?",
        onOk: function onOk() {
          _this6.operationLoading = true;
          delMenuTemplate(v.id).then(function (res) {
            _this6.operationLoading = false;

            if (res.success === true) {
              _this6.$Message.success("删除成功");

              _this6.getList();
            }
          });
        }
      });
    }
  },
  mounted: function mounted() {
    this.init();
  }
};
