import { uploadFile } from "@/api/index";
export default {
  name: "uploadPicInput",
  props: {
    width: {
      type: String,
      default: "200px"
    }
  },
  data: function data() {
    return {
      accessToken: {},
      picUrl: "",
      loading: false,
      viewImage: false,
      uploadFileUrl: uploadFile
    };
  },
  methods: {
    init: function init() {
      this.accessToken = {
        accessToken: this.getStore("accessToken")
      };
    },
    handleFormatError: function handleFormatError(file) {
      this.loading = false;
      this.$Notice.warning({
        title: "不支持的文件格式",
        desc: "所选文件‘ " + file.name + " ’格式不正确, 请选择 .jpg .jpeg .png .gif .bmp格式文件"
      });
    },
    handleMaxSize: function handleMaxSize(file) {
      this.loading = false;
      this.$Notice.warning({
        title: "文件大小过大",
        desc: "所选文件‘ " + file.name + " ’大小过大, 不得超过 5M."
      });
    },
    beforeUpload: function beforeUpload() {
      this.loading = true;
      return true;
    },
    handleSuccess: function handleSuccess(res, file) {
      this.loading = false;

      if (res.success === true) {
        file.url = res.result;
        this.picUrl = res.result;
        this.$emit("on-change", this.picUrl);
      } else {
        this.$Message.error(res.message);
      }
    },
    handleError: function handleError(error, file, fileList) {
      this.loading = false;
      this.$Message.error(file.message);
    },
    handleChange: function handleChange(v) {
      this.$emit("on-change", this.picUrl);
    },
    setPicUrl: function setPicUrl(v) {
      this.picUrl = v;
      this.$emit("on-change", this.picUrl);
    }
  },
  created: function created() {
    this.init();
  }
};
