import { getIntegralRechargeDetailsList } from "@/api/integral";
export default {
  name: "rechargeDetails",
  data: function data() {
    return {
      loading: false,
      searchForm: {
        userId: this.$route.params.userId,
        pageNumber: 1,
        pageSize: 10,
        sort: "createTime",
        order: "desc",
        // 时间
        startDate: "",
        endDate: ""
      },
      // 时间选择器
      selectDate: null,
      wxUserColumns: [{
        title: "序号",
        type: "index",
        width: 70,
        align: "center",
        fixed: "left"
      }, {
        title: "消费金额",
        key: "amount",
        minWidth: 145,
        sortable: true,
        fixed: "left"
      }, {
        title: "充值积分",
        key: "integral",
        minWidth: 145,
        sortable: true,
        fixed: "left"
      }, {
        title: "说明",
        key: "description",
        minWidth: 145,
        fixed: "left"
      }, {
        title: "创建时间",
        key: "createTime",
        sortable: true,
        sortType: "desc",
        width: 150
      }],
      wxUserData: [],
      // 分页-总记录数
      total: 0
    };
  },
  methods: {
    back: function back() {
      window.history.back();
    },
    init: function init() {
      this.getIntegraDetailslListData();
    },
    getIntegraDetailslListData: function getIntegraDetailslListData() {
      var _this = this;

      this.loading = true;
      getIntegralRechargeDetailsList(this.searchForm).then(function (res) {
        _this.loading = false;

        if (res.success) {
          _this.wxUserData = res.result.content;
          _this.total = res.result.totalElements;
        }
      });
    },
    changePage: function changePage(v) {
      this.searchForm.pageNumber = v;
      this.getIntegraDetailslListData();
    },
    changePageSize: function changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getIntegraDetailslListData();
    },
    selectDateRange: function selectDateRange(v) {
      if (v) {
        this.searchForm.startDate = v[0];
        this.searchForm.endDate = v[1];
      }
    },
    handleSearch: function handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.getIntegraDetailslListData();
    },
    handleReset: function handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.selectDate = null;
      this.searchForm.startDate = "";
      this.searchForm.endDate = "";
      this.getIntegraDetailslListData();
    },
    changeSort: function changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;

      if (e.order === "normal") {
        this.searchForm.order = "";
      }

      this.getIntegraDetailslListData();
    }
  },
  computed: {
    showName: function showName() {
      return this.$route.params.nickName;
    },
    totalIntegral: function totalIntegral() {
      return this.$route.params.totalIntegral;
    }
  },
  watch: {
    "$route.name": function $routeName(toC, fromC) {
      if (fromC === "integral-manage" && toC === "rechargeDetails") {} else {
        this.init();
      }
    }
  },
  mounted: function mounted() {
    this.init();
  }
};
