import Cookies from "js-cookie";
import { login, userInfo, githubLogin, qqLogin, weiboLogin, getJWT, sendSms, smsLogin } from "@/api/index";
import util from "@/libs/util.js";
export default {
  data: function data() {
    return {
      error: false,
      errorMsg: "",
      tabName: "username",
      saveLogin: true,
      loading: false,
      sending: false,
      sended: false,
      count: 60,
      countButton: "60 s",
      maxLength: 6,
      errorCode: "",
      form: {
        username: "",
        password: ""
      },
      rules: {
        username: [{
          required: true,
          message: "账号不能为空",
          trigger: "blur"
        }],
        password: [{
          required: true,
          message: "密码不能为空",
          trigger: "blur"
        }]
      }
    };
  },
  methods: {
    showErrorMsg: function showErrorMsg(msg) {
      this.error = true;
      this.errorMsg = msg;
    },
    countDown: function countDown() {
      var that = this;

      if (this.count === 0) {
        this.sended = false;
        this.count = 60;
        return;
      } else {
        this.countButton = this.count + " s";
        this.count--;
      }

      setTimeout(function () {
        that.countDown();
      }, 1000);
    },
    submitLogin: function submitLogin() {
      var _this = this;

      if (this.tabName === "username") {
        this.$refs.usernameLoginForm.validate(function (valid) {
          if (valid) {
            _this.loading = true;
            login({
              username: _this.form.username,
              password: _this.form.password,
              saveLogin: _this.saveLogin
            }).then(function (res) {
              if (res.success === true) {
                _this.setStore("accessToken", res.result); // 获取用户信息


                userInfo().then(function (res) {
                  if (res.success === true) {
                    // 避免超过大小限制
                    delete res.result.permissions;
                    var roles = [];
                    res.result.roles.forEach(function (e) {
                      roles.push(e.name);
                    });

                    _this.setStore("roles", roles);

                    if (_this.saveLogin) {
                      // 保存7天
                      Cookies.set("userInfo", JSON.stringify(res.result), {
                        expires: 7
                      });
                    } else {
                      Cookies.set("userInfo", JSON.stringify(res.result));
                    }

                    _this.setStore("userInfo", res.result);

                    _this.$store.commit("setAvatarPath", res.result.avatar); // 加载菜单


                    util.initRouter(_this);

                    _this.$router.push({
                      name: "home_index"
                    });
                  } else {
                    _this.loading = false;
                  }
                });
              } else {
                _this.loading = false;
              }
            });
          }
        });
      }
    },
    relatedLogin: function relatedLogin() {},
    handleDropDown: function handleDropDown(v) {}
  },
  mounted: function mounted() {}
};
