import { getIntegralList } from "@/api/integral";
import expandRow from "./expand.vue"; // import WxUserIntegral from "./wxUserIntegral.vue";

export default {
  name: "integral-manage",
  components: {
    expandRow: expandRow // WxUserIntegral

  },
  data: function data() {
    var _this = this;

    return {
      userType: 2,
      loading: false,
      searchForm: {
        // 默认显示回收人员
        who: 2,
        phone: "",
        username: "",
        // 回收人员表
        // name: "",
        // 微信用户表
        // nickName:"",
        // 分页
        pageNumber: 1,
        pageSize: 10,
        sort: "createTime",
        order: "desc",
        // 时间
        startDate: "",
        endDate: ""
      },
      // 时间选择器
      selectDate: null,
      wxUserColumns: [{
        title: "序号",
        type: "index",
        width: 70,
        align: "center",
        fixed: "left"
      }, {
        title: "人员类型",
        key: "who",
        minWidth: 145,
        align: "center",
        fixed: "left",
        render: function render(h, params) {
          var re = "";

          if (params.row.who === 2) {
            return h("div", "用户");
          } else {
            return h("div", "回收人员");
          }
        }
      }, {
        title: "用户名",
        key: "username",
        minWidth: 145,
        fixed: "left",
        align: "center"
      }, {
        title: "用户积分",
        key: "integral",
        minWidth: 145,
        sortable: true,
        align: "center"
      }, {
        title: "手机号",
        key: "phone",
        minWidth: 145,
        tooltip: true,
        align: "center",
        render: function render(h, params) {
          if (params.row.phone === null || params.row.phone === undefined || params.row.phone === "") {
            return h("div", "用户未填写手机号");
          } else {
            return h("div", params.row.phone);
          }
        }
      }, {
        title: "创建时间",
        key: "createTime",
        sortable: true,
        sortType: "desc",
        width: 150
      }, {
        title: "操作",
        key: "action",
        width: 300,
        align: "center",
        fixed: "right",
        render: function render(h, params) {
          return h("div", [h("Button", {
            props: {
              type: "info",
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.seeIntegralDetails(params.row);
              }
            }
          }, "积分明细"), h("Button", {
            props: {
              type: "success",
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.seeRechargeDetails(params.row);
              }
            }
          }, "充值明细"), h("Button", {
            props: {
              type: "primary",
              size: "small"
            },
            on: {
              click: function click() {
                _this.seeWithdrawalDetails(params.row, _this.searchForm.who);
              }
            }
          }, "提现明细")]);
        }
      }],
      // 列表数据
      // data: [],
      wxUserData: [],
      personnelData: [],
      // 分页-总记录数
      total: 0
    };
  },
  methods: {
    handleWho: function handleWho() {
      this.getIntegralListData();
    },
    // 查看积分明细
    seeIntegralDetails: function seeIntegralDetails(v) {
      this.$router.push({
        name: "integralDetails",
        params: {
          userId: v.userId,
          nickName: v.nickName,
          totalIntegral: v.integral
        }
      }); // this.$router.routes.push({name: 'integralDetails', params: {userId: v.userId, nickName: v.nickName, totalIntegral: v.integral}, component: () => import('@/views/integral/integralDetails.vue')})
    },
    // 查看充值明细
    seeRechargeDetails: function seeRechargeDetails(v) {
      this.$router.push({
        name: "rechargeDetails",
        params: {
          userId: v.userId,
          nickName: v.nickName,
          totalIntegral: v.integral
        }
      });
    },
    // 查看提现明细
    seeWithdrawalDetails: function seeWithdrawalDetails(v, who) {
      this.$router.push({
        name: "withdrawalDetails",
        params: {
          userId: v.userId,
          nickName: v.nickName,
          totalIntegral: v.integral,
          who: who
        }
      });
    },
    getIntegralListData: function getIntegralListData() {
      var _this2 = this;

      this.loading = true;
      getIntegralList(this.searchForm).then(function (res) {
        _this2.loading = false;

        if (res.success) {
          // if (this.searchForm.who === 2) {
          _this2.wxUserData = res.result.records;
          _this2.total = res.result.total;
        }
      });
    },
    changePage: function changePage(v) {
      this.searchForm.pageNumber = v;
      this.getIntegralListData();
    },
    changePageSize: function changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getIntegralListData();
    },
    selectDateRange: function selectDateRange(v) {
      if (v) {
        this.searchForm.startDate = v[0];
        this.searchForm.endDate = v[1];
      }
    },
    handleSearch: function handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.getIntegralListData();
    },
    handleReset: function handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.selectDate = null;
      this.searchForm.startDate = "";
      this.searchForm.endDate = "";
      this.getIntegralListData();
    },
    changeSort: function changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;

      if (e.order === "normal") {
        this.searchForm.order = "";
      }

      this.getIntegralListData();
    }
  },
  mounted: function mounted() {
    this.getIntegralListData();
  }
};
