/**
 * 存储localStorage
 */
export var setStore = function setStore(name, content) {
  if (!name) return;

  if (typeof content !== 'string') {
    content = JSON.stringify(content);
  }

  window.localStorage.setItem(name, content);
};
/**
 * 获取localStorage
 */

export var getStore = function getStore(name) {
  if (!name) return;
  return window.localStorage.getItem(name);
};
/**
 * 删除localStorage
 */

export var removeStore = function removeStore(name) {
  if (!name) return;
  window.localStorage.removeItem(name);
};
