import { sendOneSms, delSms, getAllSms } from "@/api/sms";
import { getUserListData, getAllRoleList, getAllUserData, getDictDataByType } from "@/api/index";
import expandRow from "./expand.vue";
import departmentChoose from "@/views/components/custom/department-choose";
import departmentTreeChoose from "@/views/components/custom/department-tree-choose";
import uploadPicInput from "@/views/components/custom/upload-pic-input";
import circleLoading from "@/views/components/custom/circle-loading.vue";
export default {
  name: "user-manage",
  components: {
    circleLoading: circleLoading,
    expandRow: expandRow,
    departmentChoose: departmentChoose,
    departmentTreeChoose: departmentTreeChoose,
    uploadPicInput: uploadPicInput
  },
  data: function data() {
    var _this = this;

    var validatePassword = function validatePassword(rule, value, callback) {
      if (value.length < 6) {
        callback(new Error("密码长度不得小于6位"));
      } else {
        callback();
      }
    };

    var validateMobile = function validateMobile(rule, value, callback) {
      var reg = /^[1][3,4,5,7,8][0-9]{9}$/;

      if (!reg.test(value)) {
        callback(new Error("手机号格式错误"));
      } else {
        callback();
      }
    };

    return {
      FormRules: {
        phoneNum: [{
          validator: validateMobile,
          trigger: 'blur'
        }]
      },
      loading: true,
      operationLoading: false,
      loadingExport: true,
      modalExportAll: false,
      drop: false,
      // dropDownContent: "展开",
      // dropDownIcon: "ios-arrow-down",
      selectCount: 0,
      selectList: [],
      dataDep: [],
      searchKey: "",
      searchForm: {
        type: -1,
        //通过判断是否-1  防止用户乱发送，0-默认类型 1-回收人员注册  2-接单通知
        phoneNum: "",
        //接收短信的手机号码
        status: -1,
        //查询状态，"状态：0-待发送 1-发送成功 2-发送失败"   传入-1代表全部
        pageNumber: 1,
        pageSize: 10,
        sort: "createTime",
        order: "desc",
        startDate: "",
        endDate: ""
      },
      selectDate: null,
      modalType: 0,
      userModalVisible: false,
      modalTitle: "",
      userForm: {
        // appointmentDay: "",
        // appointmentStartTime: "",
        // appointmentEndTime: "",
        // userId: "",
        type: -1,
        delFlag: 1,
        // recyclingPersonId: "",
        // contactsName: "",
        // contactsPhone: "",
        // contactsAdderss: "",
        // contactsFloor: 0,
        // isElevatorRoom: -1,
        // longitude: 0,
        // latitude: 0,
        // mileage: 0,
        // orderId: "",
        // estimatedPrice: 0,
        // paidPrice: 0,
        // status: -1,
        // isManual: -1
        phoneNum: "",
        //接收的手机号码
        content: "" //发送的内容

      },
      userRoles: [],
      roleList: [],
      errorPass: "",
      userFormValidate: {
        username: [{
          required: true,
          message: "账号不能为空",
          trigger: "blur"
        }],
        mobile: [{
          required: true,
          message: "手机号不能为空",
          trigger: "blur"
        }, {
          validator: validateMobile,
          trigger: "blur"
        }],
        email: [{
          required: true,
          message: "请输入邮箱地址"
        }, {
          type: "email",
          message: "邮箱格式不正确"
        }]
      },
      submitLoading: false,
      columns: [// {
      //   type: "selection",
      //   width: 60,
      //   align: "center",
      //   fixed: "left"
      // },
      {
        type: "expand",
        width: 50,
        fixed: "left",
        render: function render(h, params) {
          return h(expandRow, {
            props: {
              row: params.row
            }
          });
        }
      }, {
        type: "index",
        width: 80,
        align: "center",
        fixed: "left",
        title: "序号"
      }, {
        title: "手机号",
        align: "center",
        key: "phoneNum",
        minWidth: 145,
        sortable: true,
        fixed: "left"
      }, {
        title: "短信状态",
        align: "center",
        key: "status",
        minWidth: 145,
        sortable: true,
        fixed: "left",
        render: function render(h, params) {
          var re = "";

          if (params.row.status === 0) {
            re = "待发送";
          } else if (params.row.status === 1) {
            re = "发送成功";
          } else if (params.row.status === 2) {
            re = "发送失败";
          }

          return h("div", re);
        }
      }, {
        title: "短信类型",
        align: "center",
        key: "type",
        minWidth: 145,
        sortable: true,
        fixed: "left",
        render: function render(h, params) {
          var re = "";

          if (params.row.type === 0) {
            re = "测试";
          } else if (params.row.type === 1) {
            re = "回收人员注册";
          } else if (params.row.type === 2) {
            re = "接单提醒";
          } else if (params.row.type === 3) {
            re = "验证码";
          } else if (params.row.type === 4) {
            re = "回收人员登陆";
          } else if (params.row.type === 5) {
            re = "手动派单提醒";
          }

          return h("div", re);
        }
      }, {
        title: "发送时间",
        align: "center",
        key: "sendTime",
        minWidth: 145,
        sortable: true,
        fixed: "left"
      }, {
        title: "操作",
        align: "center",
        key: "action",
        width: 200,
        fixed: "right",
        render: function render(h, params) {
          var enableOrDisable = "";
          return h("div", [h("Button", {
            props: {
              type: "error",
              size: "small"
            },
            on: {
              click: function click() {
                _this.delOneMsg(params.row); //改成了取消订单的逻辑

              }
            }
          }, "删除短信")]);
        }
      }],
      exportColumns: [{
        title: "用户名",
        key: "username"
      }, {
        title: "头像",
        key: "avatar"
      }, {
        title: "所属部门ID",
        key: "departmentId"
      }, {
        title: "所属部门",
        key: "departmentTitle"
      }, {
        title: "手机",
        key: "mobile"
      }, {
        title: "邮箱",
        key: "email"
      }, {
        title: "性别",
        key: "sex"
      }, {
        title: "用户类型",
        key: "type"
      }, {
        title: "状态",
        key: "status"
      }, {
        title: "删除标志",
        key: "delFlag"
      }, {
        title: "创建时间",
        key: "createTime"
      }, {
        title: "更新时间",
        key: "updateTime"
      }],
      data: [],
      exportData: [],
      total: 0,
      dictSex: []
    };
  },
  methods: {
    init: function init() {
      this.getUserList();
      this.getDictSexData();
    },
    getDictSexData: function getDictSexData() {
      var _this2 = this;

      // 获取性别字典数据
      getDictDataByType("sex").then(function (res) {
        if (res.success) {
          _this2.dictSex = res.result;
        }
      });
    },
    handleSelectDepTree: function handleSelectDepTree(v) {
      this.userForm.departmentId = v[0];
    },
    handleSelectDep: function handleSelectDep(v) {
      this.searchForm.departmentId = v;
    },
    changePage: function changePage(v) {
      this.searchForm.pageNumber = v;
      this.getUserList();
      this.clearSelectAll();
    },
    changePageSize: function changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getUserList();
    },
    selectDateRange: function selectDateRange(v) {
      if (v) {
        this.searchForm.startDate = v[0];
        this.searchForm.endDate = v[1];
      }
    },
    getUserList: function getUserList() {
      var _this3 = this;

      // 多条件搜索用户列表
      this.loading = true; // 避免后台默认值

      if (!this.searchForm.type) {
        this.searchForm.type = -1; //后台通过判断值是否小于0决定是否进行条件查询，小于0则查询所有type
      }

      if (!this.searchForm.status) {
        this.searchForm.status = -1; //后台通过判断值是否小于0决定是否进行条件查询，小于0则查询所有type
      }

      getAllSms(this.searchForm).then(function (res) {
        _this3.loading = false;

        if (res.success === true) {
          _this3.data = res.result.records;
          _this3.total = res.result.total;
        } else if (res.success === false) {
          _this3.$Message.error(res.message + ",错误代码:" + res.code);
        }
      });
    },
    handleSearch: function handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.getUserList();
    },
    handleReset: function handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.selectDate = null;
      this.searchForm.startDate = "";
      this.searchForm.endDate = "";
      this.selectDep = [];
      this.searchForm.departmentId = ""; // 重新加载数据

      this.getUserList();
    },
    changeSort: function changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;

      if (e.order === "normal") {
        this.searchForm.order = "";
      }

      this.getUserList();
    },
    getRoleList: function getRoleList() {
      var _this4 = this;

      getAllRoleList().then(function (res) {
        if (res.success === true) {
          _this4.roleList = res.result;
        }
      });
    },
    handleDropdown: function handleDropdown(name) {
      var _this5 = this;

      if (name === "refresh") {
        this.getUserList();
      } else if (name === "exportData") {
        if (this.selectCount <= 0) {
          this.$Message.warning("您还未选择要导出的数据");
          return;
        }

        this.$Modal.confirm({
          title: "确认导出",
          content: "您确认要导出所选 " + this.selectCount + " 条数据?",
          onOk: function onOk() {
            _this5.$refs.exportTable.exportCsv({
              filename: "用户数据"
            });
          }
        });
      } else if (name === "exportAll") {
        this.modalExportAll = true;
      } else if (name === "importData") {
        this.$Modal.info({
          title: "暂未开发",
          content: "请期待"
        });
      }
    },
    exportAll: function exportAll() {
      var _this6 = this;

      getAllSms.then(function (res) {
        _this6.modalExportAll = false;

        if (res.success) {
          _this6.exportData = res.result;
          setTimeout(function () {
            _this6.$refs.exportTable.exportCsv({
              filename: "用户全部数据"
            });
          }, 1000);
        }
      });
    },
    cancelUser: function cancelUser() {
      this.userModalVisible = false;
    },
    submitMsg: function submitMsg() {
      var _this7 = this;

      this.$refs.userForm.validate(function (valid) {
        if (valid) {
          if (_this7.modalType === 0) {
            // 添加记录 避免编辑后传入id
            delete _this7.userForm.id;
            delete _this7.userForm.status; // var reg = /^[1][3,4,5,7,8][0-9]{9}$/;
            //  if (!reg.test(this.userForm.phoneNum)) {
            //    this.$message.error("请输入正确的手机号码");
            //    } else 

            if (_this7.userForm.type != -1) {
              _this7.submitLoading = true;
              sendOneSms(_this7.userForm).then(function (res) {
                _this7.submitLoading = false;

                if (res.success === true) {
                  _this7.$Message.success("操作成功");

                  _this7.userForm.phoneNum = "";
                  _this7.userForm.content = "";
                  _this7.userForm.type = -1; //发送成功  参数复位

                  _this7.getUserList();

                  _this7.userModalVisible = false;
                } else {
                  _this7.$Message.error(res.message + ",错误代码:" + res.code);
                }
              });
            } else {
              _this7.$Message.warning("请选择短信类型");
            }
          } else {
            // 编辑
            _this7.submitLoading = true;
            editUser(_this7.userForm).then(function (res) {
              _this7.submitLoading = false;

              if (res.success === true) {
                _this7.$Message.success("操作成功");

                _this7.getUserList();

                _this7.userModalVisible = false;
              }
            });
          }
        }
      });
    },
    handleUpload: function handleUpload(v) {
      this.userForm.avatar = v;
    },
    add: function add() {
      this.modalType = 0;
      this.modalTitle = "发送短信";
      this.$refs.userForm.resetFields();
      this.userModalVisible = true;
    },
    delOneMsg: function delOneMsg(v) {
      var _this8 = this;

      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除发送给" + v.phoneNum + "的短信?",
        onOk: function onOk() {
          delSms(v).then(function (res) {
            _this8.operationLoading = true;

            if (res.success === true) {
              _this8.$Message.success("删除成功");

              _this8.operationLoading = false;

              _this8.getUserList();
            } else if (res.success === false) {
              _this8.$Message.error(res.message) + ",错误码:" + res.code;
            }
          });
        }
      });
    },
    showSelect: function showSelect(e) {
      this.exportData = e;
      this.selectList = e;
      this.selectCount = e.length;
    },
    clearSelectAll: function clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    delAll: function delAll() {
      var _this9 = this;

      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }

      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
        onOk: function onOk() {
          var ids = "";

          _this9.selectList.forEach(function (e) {
            ids += e.id + ",";
          });

          ids = ids.substring(0, ids.length - 1);
          _this9.operationLoading = true;
          delOneOrder(ids).then(function (res) {
            _this9.operationLoading = false;

            if (res.success === true) {
              _this9.$Message.success("删除成功");

              _this9.clearSelectAll();

              _this9.getUserList();
            }
          });
        }
      });
    }
  },
  mounted: function mounted() {
    this.init();
    this.getRoleList();
  }
};
