import { getWechatContent } from "@/api/publishContent";
import { getDictDataByType } from "@/api/index";
import { types } from "util";
export default {
  name: "seeContent",
  data: function data() {
    return {
      // 内容表单
      contentForm: {
        title: "",
        author: "",
        isPublicatin: "",
        isTop: "",
        seoDescription: "",
        content: "",
        contentType: "",
        createTime: ""
      },
      dictContentType: [] // 发布内容类别

    };
  },

  /* created() {
    //导航完成后通过内容id获取全文,发送http请求
    // this.fetchData();
  }, */
  methods: {
    init: function init() {
      this.getDictContenTypeData();
      this.fetchData();
    },
    // 获取内容类别
    getDictContenTypeData: function getDictContenTypeData() {
      var _this = this;

      getDictDataByType("contentType").then(function (res) {
        if (res.success) {
          _this.dictContentType = res.result;
        }
      });
    },
    // 获取内容全文
    fetchData: function fetchData() {
      var _this2 = this;

      getWechatContent(this.$route.params.id).then(function (res) {
        if (res.success) {
          _this2.contentForm = res.result;

          _this2.dictContentType.forEach(function (e) {
            if (Number(e.value) === _this2.contentForm.contentType) {
              _this2.contentForm.contentType = e.title;
            }
          });
        }
      });
    },
    // 返回按钮
    back: function back() {
      window.history.back();
    }
  },

  /* computed: {
    showContentType: function() {
      this.dictContentType.forEach(e => {
        if (Number(e.value) === this.contentForm.contentType) {
          return e.title;
        }
      });
    },
  }, */
  // 导航完成后获取数据
  watch: {
    "$route.name": function $routeName(toC, fromC) {
      if (fromC === "contentManage" && toC === "seeContent") {
        this.init();
      }
    }
  },
  // 导航完成前获取数据

  /* beforeRouteUpdate (to, from, next) {
    getWechatContent(to.params.id, (contentForm) => {
