import { icons } from "@/libs/icon";
export default {
  name: "iconChoose",
  data: function data() {
    return {
      icon: [],
      key: "",
      tip: "输入英文关键词搜索，比如 success"
    };
  },
  methods: {
    init: function init() {
      var re = [];
      icons.forEach(function (e) {
        e.icons.forEach(function (item) {
          re.push(item);
        });
      });
      this.icon = re;
    },
    handleInput: function handleInput() {
      var _this = this;

      if (this.key) {
        // 搜索
        var re = [];
        icons.forEach(function (e) {
          e.tags.forEach(function (item) {
            if (item.indexOf(_this.key) >= 0) {
              e.icons.forEach(function (r) {
                re.push(r);
              });
            }
          });
        });
        this.icon = re;
      } else {
        this.init();
      }
    },
    handleFocus: function handleFocus() {
      if (!this.key) {
        this.tip = "";
      }
    },
    handleBlur: function handleBlur() {
      if (!this.key) {
        this.tip = "输入英文关键词搜索，比如 success";
      }
    },
    hanleChoose: function hanleChoose(v) {
      this.$emit("on-select", v);
    }
  },
  created: function created() {
    this.init();
  }
};
