import { initDepartment, loadDepartment, searchDepartment } from "@/api/index";
export default {
  name: "departmentTreeChoose",
  props: {
    multiple: {
      type: Boolean,
      default: false
    },
    width: {
      type: String,
      default: "200px"
    },
    placeholder: {
      type: String,
      default: "点击选择部门"
    }
  },
  data: function data() {
    return {
      depLoading: false,
      departmentTitle: "",
      searchKey: "",
      dataDep: [],
      selectDep: [],
      department: []
    };
  },
  methods: {
    initDepartmentData: function initDepartmentData() {
      var _this = this;

      initDepartment().then(function (res) {
        if (res.success === true) {
          res.result.forEach(function (e) {
            if (e.isParent) {
              e.loading = false;
              e.children = [];
            }

            if (e.status === -1) {
              e.title = "[已禁用] " + e.title;
              e.disabled = true;
            }
          });
          _this.dataDep = res.result;
        }
      });
    },
    loadData: function loadData(item, callback) {
      loadDepartment(item.id).then(function (res) {
        if (res.success === true) {
          res.result.forEach(function (e) {
            if (e.isParent) {
              e.loading = false;
              e.children = [];
            }

            if (e.status === -1) {
              e.title = "[已禁用] " + e.title;
              e.disabled = true;
            }
          });
          callback(res.result);
        }
      });
    },
    searchDep: function searchDep() {
      var _this2 = this;

      // 搜索部门
      if (this.searchKey) {
        this.depLoading = true;
        searchDepartment({
          title: this.searchKey
        }).then(function (res) {
          _this2.depLoading = false;

          if (res.success) {
            res.result.forEach(function (e) {
              if (e.status === -1) {
                e.title = "[已禁用] " + e.title;
                e.disabled = true;
              }
            });
            _this2.dataDep = res.result;
          }
        });
      } else {
        this.initDepartmentData();
      }
    },
    selectTree: function selectTree(v) {
      var ids = [],
          title = "";
      v.forEach(function (e) {
        ids.push(e.id);

        if (title == "") {
          title = e.title;
        } else {
          title = title + "、" + e.title;
        }
      });
      this.departmentId = ids;
      this.departmentTitle = title;
      this.$emit("on-change", this.departmentId);
    },
    clearSelect: function clearSelect() {
      this.departmentId = [];
      this.departmentTitle = "";
      this.initDepartmentData();
      this.$emit("on-change", this.departmentId);
    },
    setSelectDep: function setSelectDep(ids, title) {
      this.departmentId = ids;
      this.departmentTitle = title;
      this.$emit("on-change", this.departmentId);
    }
  },
  created: function created() {
    this.initDepartmentData();
  }
};
