import { initDepartment, loadDepartment, searchDepartment } from "@/api/index";
import { initClassify,loadClassify,searchClassify }from "@/api/recycle";
export default {
  name: "classifyTreeChoose",
  props: {
    multiple: {
      type: Boolean,
      default: false
    },
    width: {
      type: String,
      default: "200px"
    },
    placeholder: {
      type: String,
      default: "点击选分类"
    }
  },
  data() {
    return {
      depLoading: false,
      name: "",
      searchKey: "",
      dataDep: [],
      selectDep: [],
      classify: [],
      dataIndex:[]
    
    };
  },
  methods: {
    initClassifyData() {
      initClassify().then(res => { 
        if (res.success === true) {
          res.result.forEach(function(e) {
            if (e.parentId!==-1) {
              e.loading = false;
              e.children = [];
              e.title = e.name
            }
            if (e.status === -1) {
              e.title = "[已禁用] " + e.name;
              e.disabled = true;
            }
          });
          this.dataDep = res.result;
        }
      });
    },
    loadData(item, callback) {
      loadClassify(item.id).then(res => {
        if (res.success === true) {
          res.result.forEach(function(e) {
            if (e.parentId!==-1) {
              e.loading = false;
              e.children = [];
              e.title = e.name
            }
            if (e.status === -1) {
              e.title = "[已禁用] " + e.name;
              e.disabled = true;
            }
          });
          callback(res.result);
        }
      });
  
    },
    searchDep() {
      // 搜索部门
      if (this.searchKey) {
        this.depLoading = true;
        searchClassify({ title: this.searchKey }).then(res => {
          this.depLoading = false;
          if (res.success) {
            res.result.forEach(function(e) {
              if (e.status === -1) {
                e.name = "[已禁用] " + e.name;
                e.disabled = true;
              }
            });
            this.dataDep = res.result;
          }
        });
      } else {
        this.initClassifyData();
      }
    },
    selectTree(v) {
      //this.$emit("getEntity",v)
      let ids = [],
        title = "";
      v.forEach(e => {
        ids.push(e.id);
        if (title == "") {
          title = e.title;
        } else {
          title = title + "、" + e.title;
        }
      });
      this.id = ids;
      this.name = title;
      this.$emit("on-change", this.id);
    },
    clearSelect() {
      this.id = [];
      this.name = "";
      this.initClassifyData();
      this.$emit("on-change", this.id);
    },
    setSelectDep(ids, title){
      this.id = ids;
      console.log(title)
      this.name = title;
      this.$emit("on-change", this.id);
    }
  },
  created() {
    this.initClassifyData();
  }
};
