import { icons } from "@/libs/icon";
export default {
  name: "iconChoose",
  data() {
    return {
      icon: [],
      key: "",
      tip: "输入英文关键词搜索，比如 success"
    };
  },
  methods: {
    init() {
      let re = [];
      icons.forEach(e => {
        e.icons.forEach(item => {
          re.push(item);
        });
      });
      this.icon = re;
    },
    handleInput() {
      if (this.key) {
        // 搜索
        let re = [];
        icons.forEach(e => {
          e.tags.forEach(item => {
            if (item.indexOf(this.key) >= 0) {
              e.icons.forEach(r => {
                re.push(r);
              });
            }
          });
        });
        this.icon = re;
      } else {
        this.init();
      }
    },
    handleFocus() {
      if (!this.key) {
        this.tip = "";
      }
    },
    handleBlur() {
      if (!this.key) {
        this.tip = "输入英文关键词搜索，比如 success";
      }
    },
    hanleChoose(v) {
      this.$emit("on-select", v);
    }
  },
  created() {
    this.init();
  }
};
