import {
  getAccountPage,
  addAccount,
  editAccount,
  delAccount,
  getWxMenu,
  createWxMenu,
  getAllMenuTemplate
} from "@/api/wechat";
import {
  getDictDataByType
} from "@/api/index";
import { debug } from 'util';
 

export default {
  name: "builder-code",
  components: {
 
  },
  data() {
    return {
      loading: true,
      operationLoading: false,
      selectCount: 0,
      selectList: [],
      searchKey: "",
      searchForm: {
        pageNumber: 1,
        pageSize: 10,
        sort: "createTime",
        order: "desc",
        name: "",
        appid: "",
        type:null
      },
      //窗口
      modalType: 0,
      modalVisible: false,
      modalTitle: "",
      formItem: {
        name:'',
        description:'',
        type: 2,
        appid:'',
        aeskey:'',
        secret:'',
        token:''
      },
      formItemValidate: {
        name: [
          { required: true, message: "公众号名称不能为空", trigger: "blur" }
        ],
        appid: [
          { required: true, message: "AppID不能为空", trigger: "blur" }
        ],
        aeskey: [
          { required: true, message: "EncodingAESKey不能为空", trigger: "blur" }
        ],
        secret: [
          { required: true, message: "AppSecret不能为空", trigger: "blur" }
        ],
        token: [
          { required: true, message: "令牌(Token)不能为空", trigger: "blur" }
        ],
      },
      submitLoading: false,
      //table 列表字段
      columns: [
        {
          type: "selection",
          width: 60,
          align: "center",
          fixed: "left"
        },
        {
          type: "index",
          width: 60,
          align: "center",
        },
        {
          title: "公众号名称",
          key: "name",
          minWidth: 120,
          sortable: true,
        },
        {
          title: "公众号类型",
          key: "type",
          width: 115,
          sortable: true,
          render: (h, params) => {
            let re = "";
            //公众号类型：1-订阅号 2-服务号 3-企业号
            if (params.row.type === 1) {
              re = "订阅号";
            } else if (params.row.type === 2) {
              re = "服务号";
            } else if (params.row.type === 3) {
              re = "企业号";
            }
            return h("div", re);
          }
        },
        {
          title: "AppID",
          key: "appid",
          align: "center",
          minWidth: 160
        },
        {
          title: "AppSecret",
          key: "secret",
          minWidth: 250
        },
        {
          title: "令牌(Token)",
          key: "token",
          minWidth: 120
        },
        {
          title: "EncodingAESKey",
          key: "aeskey",
          minWidth: 360
        },
        {
          title: "描述",
          key: "description",
          minWidth: 100
        },
        {
          title: "创建时间",
          key: "createTime",
          sortable: true,
          sortType: "desc",
          width: 150
        },
        {
          title: "操作",
          key: "action",
          width: 280,
          align: "center",
          fixed: "right",
          render: (h, params) => {
            return h("div", [
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.edit(params.row);
                    }
                  }
                },
                "编辑"
              ),
              h(
                "Button",
                {
                  props: {
                    type: "error",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.remove(params.row);
                    }
                  }
                },
                "解绑"
              ),
              h(
                "Button",
                {
                  props: {
                    type: "info",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.createMenu(params.row);
                    }
                  }
                },
                "发布菜单"
              ),
              h(
                "Button",
                {
                  props: {
                    type: "success",
                    size: "small"
                  },
                  on: {
                    click: () => {
                      this.getMenu(params.row);
                    }
                  }
                },
                "查看菜单"
              )
            ]);
          }
        }
      ],
      data: [],
      total: 0,
      dictAccountType: [],
      //查看公众号菜单窗口
      menuModalVisible: false,
      menuModalTitle: "",
      menuData:[],
      //发布公众号菜单窗口
      createMenuModalVisible: false,
      createMenuModalTitle: "",
      
      templateData: [],
      createMenuForm: {
        name: "",
        appid: "",
        templateId: ""
      },
      createMenuFormValidate:{
        templateId: [
          { required: true, message: "请选择菜单模版", trigger: "blur" }
        ],
      }
    };
  },
  methods: {
    init() {
      this.getList();
      this.getDictAccountTypeData();
      this.loadTemplates();
    },
    //加载数字字典
    getDictAccountTypeData() {
      // 获取性别字典数据
      getDictDataByType("wx_account_type").then(res => {
        if (res.success) {
          this.dictAccountType = res.result;
        }
      });
    },
    //加载模版
    loadTemplates(){
      getAllMenuTemplate().then(res => {
        if (res.success) {
          this.templateData = res.result;
        }
      });
    },
    
    //翻页
    changePage(v) {
      this.searchForm.pageNumber = v;
      this.getList();
      this.clearSelectAll();
    },
    //每页条数
    changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getList();
    },
    //排序
    changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;
      if (e.order === "normal") {
        this.searchForm.order = "";
      }
      this.getList();
    },

    showSelect(e) {
      this.exportData = e;
      this.selectList = e;
      this.selectCount = e.length;
    },
    //清空
    clearSelectAll() {
      this.$refs.table.selectAll(false);
    },

    //搜索
    handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.getList();
    },
    //重置
    handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      // 重新加载数据
      this.getList();
    },
    //查询列表
    getList() {
      // 多条件搜索用户列表
      this.loading = true;
      getAccountPage(this.searchForm).then(res => {
        this.loading = false;
        if (res.success === true) {
          this.data = res.result.content;
          this.total = res.result.totalElements;
        }
      });
    },
   
    //新增按钮操作
    add() {
      this.modalType = 0;
      this.modalTitle = "绑定公众号";
      this.$refs.formItem.resetFields();
      this.modalVisible = true;
    },
    //编辑
    edit(v) {
      this.modalType = 1;
      this.modalTitle = "编辑公众号";
      this.$refs.formItem.resetFields();
      // 转换null为""
      for (let attr in v) {
        if (v[attr] === null) {
          v[attr] = "";
        }
      }
      let str = JSON.stringify(v);
      let data = JSON.parse(str);
      this.formItem = data;
      this.modalVisible = true;
    },
    //取消
    cancelBt() {
      this.modalVisible = false;
    },
    //提交
    submitBt() {
      this.$refs.formItem.validate(valid => {
        if (valid) {
          //添加
          if (this.modalType === 0) {
            // 添加 避免编辑后传入id
            delete this.formItem.id;
            this.submitLoading = true;
            addAccount(this.formItem).then(res => {
              this.submitLoading = false;
              if (res.success === true) {
                this.$Message.success(res.message);
                this.getList();
                this.modalVisible = false;
              }
            });
          } else {
            // 编辑
            this.submitLoading = true;
            editAccount(this.formItem).then(res => {
              this.submitLoading = false;
              if (res.success === true) {
                this.$Message.success("操作成功");
                this.getList();
                this.modalVisible = false;
              }
            });
          } 
        }
      });
    },
    //查看公众号菜单
    getMenu(v){
      let id = v.appid;
      this.loading = true;
      getWxMenu(id).then(res => {
        this.loading = false;
        if (res.success === true) {
          this.menuData = res.result.menu.buttons;
        }
      });
      this.menuModalTitle = "公众号菜单";
      this.menuModalVisible = true;
    },
    menuCancelBt(){
      this.menuModalVisible = false;
    },

    //发布公众号菜单
    createMenu(v){
      this.createMenuForm.appid = v.appid;
      this.createMenuForm.name = v.name;
      this.createMenuModalTitle = "发布菜单";
      this.createMenuModalVisible = true;
    },
    createMenuCancelBt(){
      this.createMenuModalVisible = false;
    },
    createMenusubmitBt() {
      this.$refs.createMenuForm.validate(valid => {
        if (valid) {
          this.submitLoading = true;
          createWxMenu(this.createMenuForm).then(res => {
            this.submitLoading = false;
            if (res.success === true) {
              this.$Message.success(res.message);
              this.getList();
              this.createMenuModalVisible = false;
            }
          });
        }
      });
    },

    //批量删除
    delAll() {
      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要解绑的公众号");
        return;
      }
      this.$Modal.confirm({
        title: "确认解绑",
        content: "您确认要解绑所选的 " + this.selectCount + " 条数据?",
        onOk: () => {
          let ids = "";
          this.selectList.forEach(function(e) {
            ids += e.id + ",";
          });
          ids = ids.substring(0, ids.length - 1);
          this.operationLoading = true;
          delAccount(ids).then(res => {
            this.operationLoading = false;
            if (res.success === true) {
              this.$Message.success("解绑成功");
              this.clearSelectAll();
              this.getList();
            }
          });
        }
      });
    },
    //单个删除
    remove(v) {
      this.$Modal.confirm({
        title: "确认解绑",
        content: "您确认要解绑公众号【" + v.name + "】?",
        onOk: () => {
          this.operationLoading = true;
          delAccount(v.id).then(res => {
            this.operationLoading = false;
            if (res.success === true) {
              this.$Message.success("解绑成功");
              this.getList();
            }
          });
        }
      });
    },
  
    
  },
  mounted() {
    this.init();
  }
};
