import { getIntegralDetailsList } from "@/api/integral";
export default {
  name: "integralDetails",
  data() {
    return {
      loading: false,
      searchForm: {
        userId: this.$route.params.userId,

        pageNumber: 1,
        pageSize: 10,
        sort: "createTime",
        order: "desc",
        // 时间
        startDate: "",
        endDate: ""
      },
      // 时间选择器
      selectDate: null,
      wxUserColumns: [
        {
          title: "序号",
          type: "index",
          width: 70,
          align: "center",
          fixed: "left"
        },
        {
          title: "积分",
          key: "integral",
          minWidth: 145,
          sortable: true,
          fixed: "left"
        },
        {
          title: "说明",
          key: "description",
          minWidth: 145,
          fixed: "left"
        },
        {
          title: "创建时间",
          key: "createTime",
          sortable: true,
          sortType: "desc",
          width: 150
        }
      ],
      wxUserData: [],
      // 分页-总记录数
      total: 0
    };
  },
  methods: {
    back() {
      window.history.back();
    },
    init() {
      this.getIntegraDetailslListData();
    },
    getIntegraDetailslListData() {
      this.loading = true;
      getIntegralDetailsList(this.searchForm).then(res => {
        this.loading = false;
        if (res.success) {
          this.wxUserData = res.result.content;
          this.total = res.result.totalElements;
        }
      });
    },
    changePage(v) {
      this.searchForm.pageNumber = v;
      this.getIntegraDetailslListData();
    },
    changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getIntegraDetailslListData();
    },
    selectDateRange(v) {
      if (v) {
        this.searchForm.startDate = v[0];
        this.searchForm.endDate = v[1];
      }
    },
    handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.getIntegraDetailslListData();
    },
    handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.selectDate = null;
      this.searchForm.startDate = "";
      this.searchForm.endDate = "";
      this.getIntegraDetailslListData();
    },
    changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;
      if (e.order === "normal") {
        this.searchForm.order = "";
      }
      this.getIntegraDetailslListData();
    }
  },
  computed: {
    showName: function() {
      return this.$route.params.nickName;
    },
    totalIntegral: function() {
      return this.$route.params.totalIntegral;
    }
  },
  watch: {
    "$route.name"(toC, fromC) {
      if (fromC === "integral-manage" && toC === "integralDetails") {
        console.log("diaoyongl")
      } else {
        this.init();
      }
    }
  },
  mounted() {
    this.init();
  }
};
