import {
  getVerifyPersonById,
  changeStatusToPass,
  changeStatusToFaild
} from "@/api/miniUser";
import { truncate } from "fs";
export default {
  //入口
  created() {
    console.log("-----------");
    var data = this.$route.query.verifyData;
    console.log(this.$route);
    if (data != null && data != undefined && data.id != undefined) {
      this.verifyDetail = data;
      this.setStore("id", data.id);
    } else {
      this.verifyDetail.id = this.getStore("id");
    }
  },
  data() {
    return {
      imgModal: false, //查看大图
      bigImgUrl: null, //大图链接
      isBigImg: false,
      isVerify: null,
      operationLoading: false,
      // isRefuse: null,
      // isCancel: null,
      // isFinish: null,
      // isPay: null,
      color: "color",
      //前一个页面传来的数据
      verifyDetail: {
        id: "", //主键
        idCard: "",
        phone: "", //回收人员手机号码-
        type: -1, //回收人员类型 --- 后台通过判断值是否小于0决定是否进行条件查询，小于0则查询所有type-
        positiveImgUrl: "", //身份证正面照片
        reverseImgUrl: "", //身份证反面照片
        address: "", //回收人员地址
        name: "", //回收人员姓名-
        status: -1 //回收人员状态 -后台通过判断值是否小于0决定是否进行条件查询，小于0则查询所有type
      },
      
    };

  },
  computed: {
    type: function(){
      if(this.verifyDetail.type == 0){
        return "社会回收人员";
      }else if(this.verifyDetail.type == 1){
        return "公司员工";
      }
    },
    whichStatus: function(){
      if(this.verifyDetail.status==0){
        return 0;
      }else if(this.verifyDetail.status==1){
        return 1;
      }else if(this.verifyDetail.status==2){
        return 2;
      }
    },
    isNoPass: function(){
      if (this.verifyDetail.status == 0) {
        return true;
      } else return false;
    },
    isPassOrWait: function(){
      if (this.verifyDetail.status ==0 || this.verifyDetail.status ==1){
        return true;
      }else return false;
    },
    isPass: function(){
      if(this.verifyDetail.status==1){
        return true;
      }else return false;
    },
    isFaild: function(){
      if(this.verifyDetail.status==2){
        return true;
      }else return false;
    }
  },
  methods: {
    go(){
      this.$router.push({path:'recyclingPersonnelManage'});
    },
    back() {
      this.$router.go(-1);
    },
    watchBigImg(v) {
      this.imgModal = !this.imgModal;
      this.bigImgUrl = v;
    },
    //发起请求根据ID获取回收人员信息
    getVerifyMethod() {
      console.log("-" + this.verifyDetail.id);
      getVerifyPersonById(this.verifyDetail.id).then(res => {
        if (res.success === true) {
          this.verifyDetail = res.result; // 数据渲染
        }
      });
    },
    //审核通过,改变用户的状态
    verifyPass() {
      console.log("-------");
      this.$Modal.confirm({
        title: "审核",
        content: "您确定该用户通过审核吗",
        onOk: () => {
          console.log("-------");
          // console.log("---" + JSON.stringify(this));
              this.operationLoading = true;
               changeStatusToPass(this.verifyDetail.id).then(res => {
                 this.operationLoading = false;
                 if (res.success === true) {
                   this.$Message.success("该用户通过审核")
                   this.getVerifyMethod();
                  }
                });
        }
      });
    },
    //审核不通过
    verifyFaild(){
      this.$Modal.confirm({
        title:"审核",
        content:"确定该用户审核不通过吗？",
        onOk: ()=>{
          this.operationLoading = true;
          changeStatusToFaild(this.verifyDetail.id).then(res =>{
            this.operationLoading = false;
            if(res.success === true){
              this.$Message.error("审核不通过")
              this.getVerifyMethod();             
            }
          });
        }
      });
    },
  },
  //渲染完毕
  mounted() {
    console.log("----aaa" + this.verifyDetail.id);
    this.getVerifyMethod(); //重新获取回收人员信息
  }
  
};
