import{
  getEstateInfo,
  selectByCode,
  deleteEstate,
  editEstate,
  addEstate,
  findScheduleByCode,
  findByCodes
}from "@/api/community";
import {
  getAllRoleList,
  getAllUserData,
  getDictDataByType
} from "@/api/index";
import expandRow from "./expand.vue";
import departmentChoose from "@/views/components/custom/department-choose";
import departmentTreeChoose from "@/views/components/custom/department-tree-choose";
import uploadPicInput from "@/views/components/custom/upload-pic-input";
import circleLoading from "@/views/components/custom/circle-loading.vue";
import qrCode from '@/views/components/qrCode/qrCode.vue'

export default {
  name: "user-manage",
  components: {
    circleLoading,
    expandRow,
    departmentChoose,
    departmentTreeChoose,
    uploadPicInput,
    qrCode
  },
  data() {
    const isHave = (rule, value, callback)=>{
      // console.log("----"+this.modalType)
      if(this.modalType===0||(this.modalType===1&&this.oldCode!=this.userForm.code)){
           selectByCode({code:this.userForm.code}).then(res=>{
            if(res.success===true){
              callback(new Error('该编码已被注册'))
              // console.log("----"+res.result)
            }else if(res.success===false){
              callback();
            }else{
              callback(new Error('小区编码不能为空'))
            }
          })
      }else{
         callback();
      }
    };
    const isEmpty = (rule, value, callback) => {
      if (value == null || value == undefined || (value === "")) {
      callback(new Error('必填项不能为空'))
      } else {
      callback();
      }
    };
    const checkLongitude = (rule, value, callback) =>{
      let longrg = /^(\-|\+)?(((\d|[1-9]\d|1[0-7]\d|0{1,3})\.\d{0,6})|(\d|[1-9]\d|1[0-7]\d|0{1,3})|180\.0{0,6}|180)$/;
      if(!longrg.test(value)){
      callback(new Error('经度整数部分为0-180,小数部分为0到6位!')) 
    } else {
      callback();
      }
    };
    const checkLatitude = (rule, value, callback) =>{
      let latrg = /^(\-|\+)?([0-8]?\d{1}\.\d{0,6}|90\.0{0,6}|[0-8]?\d{1}|90)$/;
      if(!latrg.test(value)){
      callback(new Error('纬度整数部分为0-90,小数部分为0到6位!')) 
    } else {
      callback();
    }
    };
    const checkInt =(rule, value, callback) =>{
      if(!(Number(value)&&(value)%1 === 0)){
        callback(new Error("请输入整数编码"));
      }else{
        callback();
      }
    };
    return {
      qrCode:'',
      drawer:false,
      showCode:"",
      showName:"",
      pStyle: {
                    fontSize: '16px',
                    color: 'rgba(0,0,0,0.85)',
                    lineHeight: '24px',
                    display: 'block',
                    marginBottom: '16px'
                },
      modalQrCode:false,
      modalMap:false,
      loading: true,
      operationLoading: false,
      loadingExport: true,
      modalExportAll: false,
      drop: false,
      oldCode:"",
      dropDownContent: "展开",
      dropDownIcon: "ios-arrow-down",
      selectCount: 0,
      selectList: [],
      dataDep: [],
      searchKey: "",
      searchForm: {
        name: "",
        pageNumber: 1,
        city:"",
        address:"",
        area:"",
        code:"",
        description:"",
        pageSize: 10,
        sort: "createTime",
        order: "desc",
        startDate: "",
        endDate: ""
      },
      selectDate: null,
      modalType: 0,
      userModalVisible: false,
      modalTitle: "",
      userForm: {
        id:"",
        longitude:"",
        latitude:"",
        qrCode:"",
        name: "",
        code:"",
        city:"",
        area:"",
        address:"",
        description: "",
      },
      userRoles: [],
      roleList: [],
      errorPass: "",
      userFormValidate: {
        city:[
          {required:true,message:"所属城市不能为空",trigger: "blur"}
        ],
        area:[
          {required:true,message:"所属区县不能为空",trigger:"blur"}
        ],
        name: [
          { required: true, message: "小区名称不能为空", trigger: "blur" }
        ],
        code: [
          { required: true, message: "小区编码不能为空", trigger: "blur" },
          { validator: isHave,trigger: "blur"},
          { validator: checkInt}
        ],
        address: [
          { required: true, message: "地址不能为空", trigger: "change" }
        ],
        longitude: [
          { required: true, message: "经度不能为空", trigger: "blur" },
          { validator:checkLongitude,trigger:'blur'}
        ],
        latitude: [
          { required: true, message: "纬度不能为空", trigger: "blur" },
          { validator:checkLatitude, trigger: 'blur'} 
        ]
      },
      submitLoading: false,
      columns: [
        {
          type: "selection",
          width: 60,
          align: "center",
          fixed: "left"
        },
        {
          type: "expand",
          width: 50,
          fixed: "left",
          render: (h, params) => {
            return h(expandRow, {
              props: {
                row: params.row
              }
            });
          }
        },
        {
          type: "index",
          width: 80,
          align: "center",
          fixed: "left",
          title:'序号'
        },
        {
          title: "小区名称",
          key: "name",
          minWidth: 145,
          fixed: "left"
        },
        {
          title: "小区编码",
          key: "code",
          minWidth: 115,
          sortable: true
        },
        {
          title: "所在城市",
          key: "city",
          minWidth: 120,
        },
        {
          title:"所在区县",
          key:"area",
          minWidth:180,
        },
        {
          title: "详细地址",
          key:"address",
          minWidth:150,
        },
        {
          title:"小区描述",
          key:"description",
          minWidth:150
        },
        {
          title: "创建时间",
          key: "createTime",
          sortable: true,
          sortType: "desc",
          minWidth: 150
        },
        {
          title: "操作",
          key: "action",
          minWidth: 300,
          align: "center",
          fixed: "right",
          render: (h, params) => {
            let enableOrDisable = "";
            if (params.row.status == 0) {
              enableOrDisable = h(
                "Button",
                {
                  props: {
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.disable(params.row);
                    }
                  }
                },
                "禁用"
              );
            } else {
              enableOrDisable = h(
                "Button",
                {
                  props: {
                    type: "success",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.enable(params.row);
                    }
                  }
                },
                "启用"
              );
            }
            return h("div", [
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.oldCode = params.row.code;
                      this.edit(params.row);
                    }
                  }
                },
                "编辑"
              ),
              // enableOrDisable,
              h(
                "Button",
                {
                  props: {
                    type: "error",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      findScheduleByCode({code:params.row.code}).then(res =>{
                        this.loading = false;
                        if(res.result.length === 0){
                          this.remove(params.row);
                        }else{
                          this.$Message.error("该小区含有排班信息，不能删除")
                        }
                     });
                    }
                  }
                },
                "删除"
              ),
              h(
                "Button",
                {
                  props:{
                    type:"info",
                    size:"small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on:{
                    click:() =>{
                      this.show(params.row);
                    }
                  }
                },
                "排班信息"
              ),
              h(
                "Button",
                {
                  props:{
                    type:"success",
                    size:"small"
                  },
                  on:{
                    click:() =>{
                      this.generateQrCode(params.row);
                    }
                  }
                },
                "生成二维码"
              )
            ]);
          }
        }
      ],
      ScheduleList:[
        {
          title:"星期",
          key:"week"
        },
        {
          title:"回收车辆",
          key:"recoveryCar"
        },
        {
          title:"回收物品",
          key:"recoveryDesc",
          className: 'demo-table-info-column'

        },
        {
          title:"到达小区时间段",
          width:150,
          key:"time"
        },
        {
          title:"收运时间",
          key:"recoveryTime",
          className: 'demo-table-info-column'
        }
      ],
      exportColumns: [
        {
          title: "用户名",
          key: "username"
        },
        {
          title: "头像",
          key: "avatar"
        },
        {
          title: "所属部门ID",
          key: "departmentId"
        },
        {
          title: "所属部门",
          key: "departmentTitle"
        },
        {
          title: "手机",
          key: "mobile"
        },
        {
          title: "邮箱",
          key: "email"
        },
        {
          title: "性别",
          key: "sex"
        },
        {
          title: "用户类型",
          key: "type"
        },
        {
          title: "状态",
          key: "status"
        },
        {
          title: "删除标志",
          key: "delFlag"
        },
        {
          title: "创建时间",
          key: "createTime"
        },
        {
          title: "更新时间",
          key: "updateTime"
        }
      ],
      data: [],
      schedule:[],
      exportData: [],
      total: 0,
      dictSex: []
    };
  },
  methods: {
    go(){
      this.$router.push({path:'recyclingEstateSchedule'});

    },
    init() {
      this.getUserList();
      this.getDictSexData();
    },
    handlerMap(){
      this.modalMap = true
    },
    getDictSexData() {
      // 获取性别字典数据
      getDictDataByType("sex").then(res => {
        if (res.success) {
          this.dictSex = res.result;
        }
      });
    },
    handleSelectDepTree(v) {
      this.userForm.departmentId = v[0];
    },
    handleSelectDep(v) {
      this.searchForm.departmentId = v;
    },
    changePage(v) {
      this.searchForm.pageNumber = v;
      this.getUserList();
      this.clearSelectAll();
    },
    changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getUserList();
    },
    selectDateRange(v) {
      if (v) {
        this.searchForm.startDate = v[0];
        this.searchForm.endDate = v[1];
      }
    },
    getUserList() {
      // 多条件搜索用户列表
      this.loading = true;
      // 避免后台默认值
      // if (!this.searchForm.type) {
      //   this.searchForm.type = "";
      // }
      // if (!this.searchForm.status) {
      //   this.searchForm.status = "";
      // }
      getEstateInfo(this.searchForm).then(res => {
        this.loading = false;
        if (res.success === true) {
          this.data = res.result.content;
          this.total = res.result.totalElements;
        }
      });
    },
    handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.getUserList();
    },
    handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.selectDate = null;
      this.searchForm.startDate = "";
      this.searchForm.endDate = "";
      this.selectDep = [];
      this.searchForm.departmentId = "";
      // 重新加载数据
      this.getUserList();
    },
    changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;
      if (e.order === "normal") {
        this.searchForm.order = "";
      }
      this.getUserList();
    },
    getRoleList() {
      getAllRoleList().then(res => {
        if (res.success === true) {
          this.roleList = res.result;
        }
      });
    },
    handleDropdown(name) {
      if (name === "refresh") {
        this.getUserList();
      } else if (name === "exportData") {
        if (this.selectCount <= 0) {
          this.$Message.warning("您还未选择要导出的数据");
          return;
        }
        this.$Modal.confirm({
          title: "确认导出",
          content: "您确认要导出所选 " + this.selectCount + " 条数据?",
          onOk: () => {
            this.$refs.exportTable.exportCsv({
              filename: "用户数据"
            });
          }
        });
      } else if (name === "exportAll") {
        this.modalExportAll = true;
      } else if (name === "importData") {
        this.$Modal.info({
          title: "暂未开发",
          content: "请期待"
        });
      }
    },
    exportAll() {
      getAllUserData().then(res => {
        this.modalExportAll = false;
        if (res.success) {
          this.exportData = res.result;
          setTimeout(() => {
            this.$refs.exportTable.exportCsv({
              filename: "用户全部数据"
            });
          }, 1000);
        }
      });
    },
    cancelUser() {
      this.userModalVisible = false;
    },
    submitUser() {
      this.$refs.userForm.validate(valid => {
        if (valid) {
          if (this.modalType === 0) {
            // 添加用户 避免编辑后传入id
            delete this.userForm.id;
            this.submitLoading = true;
            addEstate(this.userForm).then(res => {
              this.submitLoading = false;
              if (res.success === true) {
                this.$Message.success("操作成功");
                this.getUserList();
                this.userModalVisible = false;
              }
            });
          } else {
            // 编辑
            this.submitLoading = true;
            editEstate(this.userForm).then(res => {
              this.submitLoading = false;
              if (res.success === true) {
                this.$Message.success("操作成功");
                this.getUserList();
                this.userModalVisible = false;
              }
            });
          }
        }
      });
    },
    handleUpload(v) {
      this.userForm.positiveImgUrl = v;
      this.userForm.reverseImgUrl = v;
    },
    generateQrCode(v){
        this.modalQrCode = true
        this.qrCode = v.code
    },
    add() {
      this.modalType = 0;
      this.modalTitle = "添加小区信息";
      this.$refs.userForm.resetFields();
      this.userModalVisible = true;
    },
    edit(v) {
      this.modalType = 1;
      this.modalTitle = "编辑小区信息";
      this.$refs.userForm.resetFields();
      // 转换null为""
      for (let attr in v) {
        if (v[attr] === null) {
          v[attr] = "";
        }
      }
      let str = JSON.stringify(v);
      let data = JSON.parse(str);
      data.longitude = String(data.longitude)
      data.latitude = String(data.latitude)
      this.userForm = data;
      this.userModalVisible = true;
    },
    enable(v) {
      this.$Modal.confirm({
        title: "确认启用",
        content: "您确认要启用用户 " + v.name + " ?",
        onOk: () => {
          this.operationLoading = true;
          enableUser(v.id).then(res => {
            this.operationLoading = false;
            if (res.success === true) {
              this.$Message.success("操作成功");
              this.getUserList();
            }
          });
        }
      });
    },
    disable(v) {
      this.$Modal.confirm({
        title: "确认禁用",
        content: "您确认要禁用用户 " + v.name + " ?",
        onOk: () => {
          this.operationLoading = true;
          disableUser(v.id).then(res => {
            this.operationLoading = false;
            if (res.success === true) {
              this.$Message.success("操作成功");
              this.getUserList();
            }
          });
        }
      });
    },
    remove(v) {
      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除改小区信息吗 " + v.name + " ?",
        onOk: () => {
          this.operationLoading = true;
          deleteEstate(v.id).then(res => {
            this.operationLoading = false;
            if (res.success === true) {
              this.$Message.success("删除成功");
              this.getUserList();
            }
          });
        }
      });
    },
    show(v){
      this.drawer=true;
      this.showName=v.name;
      this.showCode=v.code;
      findScheduleByCode({code:v.code}).then(res =>{
        this.loading = false;
        if(res.success === true){
          this.schedule = res.result;
        }
      });
    },
    dropDown() {
      if (this.drop) {
        this.dropDownContent = "展开";
        this.dropDownIcon = "ios-arrow-down";
      } else {
        this.dropDownContent = "收起";
        this.dropDownIcon = "ios-arrow-up";
      }
      this.drop = !this.drop;
    },
    showSelect(e) {
      this.exportData = e;
      this.selectList = e;
      this.selectCount = e.length;
    },
    clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    delAll() {
      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }
      let ids = "";
      this.selectList.forEach(function(e) {
        ids += e.code + ",";
      });
      ids = ids.substring(0, ids.length - 1);
      this.operationLoading = true;
      findByCodes(ids).then(res=>{
          this.operationLoading = false;
          if(res.result.length===0){
              this.$Modal.confirm({
              title: "确认删除",
              content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
              onOk: () => {
                this.operationLoading = true;
                let ids = "";
                this.selectList.forEach(function(e) {
                  ids += e.id + ",";
                });
                ids = ids.substring(0, ids.length - 1);
                deleteEstate(ids).then(res => {
                  this.operationLoading = false;
                  if (res.success === true) {
                    this.$Message.success("删除成功");
                    this.clearSelectAll();
                    this.getUserList();
                  }
                });
              }
            });
          }else{
            this.$Modal.error({
                title: '错误提示',
                content: '在您选择的小区中,其中小区编码为【'+res.result.toString()+"】的小区有排班信息,请重新选择或者先删除该小区下的所有排班信息！！！"
            });
          }
      })
      
    },
    getCoordinate(v){
      this.userForm.longitude= String(v.longitude)
      this.userForm.latitude = String(v.latitude)
      this.userForm.address =v.address
      this.modalMap = false
    }
  },
  mounted() {
    this.init();
    this.getRoleList();
  }
};
